package com.example.competition1;

import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ListView;
import android.widget.ScrollView;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import net.daum.mf.map.api.MapView;

import java.util.ArrayList;

public class CurrentSituationActivity extends AppCompatActivity implements View.OnClickListener {

    private long backKeyPressedTime = 0;
    private ScrollView scrollView=null;
    private  MapView mapView;
    private ListView currentHistoryView;
    private CurrentSituationAdapter currentHistoryAdapter;
    private ArrayList<CurrentSituation> currentHistoryList;

    @Override
    public void onCreate(Bundle savedInstanceState){
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_current_situation);

        //카카오맵
        mapView = new MapView(this);
        ViewGroup mapViewContainer = (ViewGroup) findViewById(R.id.map_view);
        mapViewContainer.addView(mapView);


        scrollView = findViewById(R.id.current_scrollview);
        Button mapButton = findViewById(R.id.current_show_map);
        Button tableButton = findViewById(R.id.current_show_table);
        mapButton.setOnClickListener(this);
        tableButton.setOnClickListener(this);

        setCurrentHistoryList();

        currentHistoryAdapter = new CurrentSituationAdapter(getApplicationContext(), currentHistoryList);  //어뎁터
        currentHistoryView.setAdapter(currentHistoryAdapter);
    }

    private void setCurrentHistoryList(){

        currentHistoryView = (ListView) findViewById(R.id.current_listview);  //신고 내역 리스트가 보여지는 뷰
        currentHistoryList = new ArrayList<>();                                  //신고 내역 리스트

        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "과자", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "새우깡", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "휴대폰", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "과자", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "새우깡", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "휴대폰", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "과자", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "새우깡", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터
        currentHistoryList.add(new CurrentSituation("data:image/jpeg;base64,", "휴대폰", "답변 대기중"));         //신고내역 리스트에 들어갈 데이터

    }


    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        switch (item.getItemId()){
            case android.R.id.home:{ //toolbar의 back키 눌렀을 때 동작
                finish();
                return true;
            }
        }
        return super.onOptionsItemSelected(item);
    }

    @Override
    public void onClick(View view) {

        switch (view.getId()) {

            case R.id.current_show_map:
                mapView.setVisibility(View.VISIBLE);
                scrollView.setVisibility(View.INVISIBLE);
                break;

            case R.id.current_show_table:
                mapView.setVisibility(View.INVISIBLE);
                scrollView.setVisibility(View.VISIBLE);
                break;
        }
    }

    // 뒤로가기 2번 누르면 종료
    @Override
    public void onBackPressed() {

        if (System.currentTimeMillis() > backKeyPressedTime + 1500) {
            backKeyPressedTime = System.currentTimeMillis();
            Toast.makeText(this, "한번더 버튼을 누르시면 종료됩니다.", Toast.LENGTH_SHORT).show();
            return;
        }

        if (System.currentTimeMillis() <= backKeyPressedTime + 1500) {
            moveTaskToBack(true);
            finishAndRemoveTask();
            System.exit(0);
        }
    }
}
